import {useState} from "react";

export default function ItemCount(props) {    
    const [Count, setCount] = useState(0)

    const sumarContador = () => {
        if (Count < props.stock) {
            setCount(Count + 1)
        }
        else {
            alert('La Cantidad supera el stock disponible')
        }
    }

    const restarContador = () => {
        if (Count > 1) {
            setCount(Count - 1)
        }
        else {
            alert('¿Eliminar item del carrito?')
            setCount(0)
        }
    }

    const agregarCarrito = () => {
        alert("Producto agragado al carrito")
    }
            
    return(
        <>
            <button class='btn btn-primary' onClick={sumarContador}>+</button>
            <p>{Count}</p>
            <button class='btn btn-primary' onClick={restarContador}>-</button>
            <button class='btn btn-primary' onClick={agregarCarrito}>Agregar al carrito</button>
        </>
    )
}
