import React, { Component, useState, useEffect } from 'react'

import 'bootstrap-icons/font/bootstrap-icons.css';

import { getProductos } from '../Backend/getProductos';


const Item = (props) => {
    const [products, setProductos] = useState([])
    const [loading, setCarga] = useState(true)

    useEffect(() => {
    getProductos
    .then(data => {
        console.log('Llamado al "Backend"')   
        setProductos(data)     
    })
    //.then(resp => console.log(resp))
    .catch(err => console.log(err))    
    .finally(()=> setCarga(false))

    return () => {
        console.log('clean')
    }
    }, [])

    return (
        <>
            { loading ?
                <img src='https://c.tenor.com/I6kN-6X7nhAAAAAj/loading-buffering.gif' alt="Cargando productos..." />
                    :  products.map(prod =>
                                    <div key={prod.id} className="card w-50 mt-5" >
                                        <div className="card-header">
                                            {prod.nombre}
                                        </div>
                                        <div className="card-body">
                                            <img src={prod.imagen} alt="Imagen" />
                                            {prod.price}
                                        </div>
                                        <div className="card-footer">
                                            <button className="btn btn-outline-primary btn-block">
                                                Detalle del producto
                                            </button>
                                        </div>   
                                    </div> 
                                    )
            } 
        </>
    );
}

export default Item
