import React, { Component, useState, useEffect } from 'react'

import { getProductos } from '../Backend/getProductos';

import './Item.css';

function Item(props) {
    const [products, setProductos] = useState([]);
    const [loading, setCarga] = useState(true);

    useEffect(() => {
        getProductos
            .then(data => {
                console.log('Llamado al "Backend"');
                setProductos(data);
            })
            .catch(error => { console.log(error); })
            .finally(() => { setCarga(false); });
    }, []);

    return (
        <>
            {loading ?
                <img src='https://c.tenor.com/I6kN-6X7nhAAAAAj/loading-buffering.gif' alt="Cargando productos..." />
                : products.map(prod => <div class="card mb-3">
                    <img src={prod.imagen} class="card-img-top" alt="Producto" />
                    <div class="card-body">
                        <h5 class="card-title">{prod.nombre}</h5>
                        <p class="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
                        <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
                    </div>
                    <div className="card-footer">
                        <button className="btn btn-outline-primary btn-block">
                            Detalle del producto
                        </button>
                    </div>
                </div>
                )}
        </>
    );
}

export default Item
