import React from 'react';
import { useEffect, useState } from 'react'
import { Productos } from '../Backend/getProductos.js';

import ItemList from '../Components/ItemList.jsx'

const ItemListContainer = (props) =>{
    const [productos, setProductos] = useState([]);
    const [loading, setLoading] = useState(true);
   
    useEffect(() => {
        const promesa = new Promise ((aceptada, rechazada) => {
            setTimeout(()=> {
                // Todo OK:
                aceptada(Productos)
                // Todo MAL:
                // rechazada('Productos no encontrados')
            }, 2000)
        })
        promesa.then((Productos) => {
            switch (props.filter) {
                case 'atos':
                    setProductos(Productos.filter(producto => producto.categoria === 'atos'))
                    break;
                case 'mods':
                    setProductos(Productos.filter(producto => producto.categoria === 'mods'))
                    break;
                case 'acc':
                    setProductos(Productos.filter(producto => producto.categoria === 'acc'))
                    break;
                default:
                    setProductos(Productos)
                    break;
            }
        })
        .catch((error)=>{
            console.log('ERROR: ' + error);
        })
        .finally( () => {
            setLoading(false)
        })
    }, [])

    return(
        <div className='Center'>
            <br />
            <h1>{props.titulo}</h1>
            {
                loading
                    ?
                <>
                <br /><br /><br />
                <img src='https://c.tenor.com/I6kN-6X7nhAAAAAj/loading-buffering.gif' alt="Cargando productos..." />
                </>
                    :
                <ItemList lista={productos}/>
            }
        </div>
    )

    this.forceUpdate();
};

export default ItemListContainer
